    private void grafikPendapatanTenant() {
        try {
            String query = "SELECT namatenant, SUM(pendapatanbersih) AS tenant FROM rekapitulasi_mingguan GROUP BY id_tenant";
            JDBCCategoryDataset dataset = new JDBCCategoryDataset(connection.getCon(), query);
            JFreeChart chart = ChartFactory.createBarChart3D("Grafik Pendapatan Tenant", "Nama Tenant", "Total Pendapatan Tenant", dataset, PlotOrientation.VERTICAL, false, true, true);
            BarRenderer renderer = null;
            CategoryPlot plot = null;
            renderer = new BarRenderer();
            ChartFrame frame = new ChartFrame("Grafik Pendapatan Tenant", chart);
            frame.setVisible(true);
            frame.setSize(1200,700);
            frame.setLocationRelativeTo(null);
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null,e);
        }
    }
    
    private void grafikPendapatanKedai() {
        try {
            String query = "SELECT tanggalrekap, keuntungan FROM rekapitulasi_bulanan";
            JDBCCategoryDataset dataset = new JDBCCategoryDataset(connection.getCon(), query);
            JFreeChart chart = ChartFactory.createLineChart("Grafik Pendapatan Bulanan", "Tanggal Rekap", "Keuntungan Per Bulan", dataset, PlotOrientation.VERTICAL, false, true, true);
            BarRenderer renderer = null;
            CategoryPlot plot = null;
            renderer = new BarRenderer();
            ChartFrame frame = new ChartFrame("Grafik Pendapatan Per Bulan", chart);
            frame.setVisible(true);
            frame.setSize(1200,700);
            frame.setLocationRelativeTo(null);
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null,e);
        }
    }
    
