    private void btCetakActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btCetakActionPerformed
         int ok = JOptionPane.showConfirmDialog(null, "Ingin Menampilkan Grafik?", "Konfirmasi", JOptionPane.YES_NO_OPTION);

            if /*(!isError)*/ (ok == 0) {
                String GR = cbGrafik.getSelectedItem().toString();
                switch (GR) {
                    case "Pendapatan Kotor Tenant":
                        grafikPendapatanTenant();
                        break;
                    case "Pendapatan Per Bulan":
                        grafikPendapatanKedai();
                        break;
                    case "Pesanan Menu Pelanggan":
                        grafikMenu();
                        break;
                    default:
                        JOptionPane.showMessageDialog(null, "Anda belum memilih laporan!");
                        break;
                }
        }
    }//GEN-LAST:event_btCetakActionPerformed

