    private void btCetakReportActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btCetakReportActionPerformed
         if (tgl1.getDate().equals("") || tgl2.getDate().equals("")) {
            JOptionPane.showMessageDialog(null, "Masukkan Tanggal Terlebih Dahulu!", "SDIT Bahrul Fikri", JOptionPane.INFORMATION_MESSAGE);
        } else {
            HashMap parameter = new HashMap();
            //boolean isError = false;
            int ok = JOptionPane.showConfirmDialog(null, "Ingin Mencetak Laporan?", "Konfirmasi", JOptionPane.YES_NO_OPTION);

            if /*(!isError)*/ (ok == 0) {
                String LP = cbReportKedai.getSelectedItem().toString();
                switch (LP) {
                    case "Pemesanan":
                        previewReport("pemesanan.jasper", parameter, LP);
                        break;
                    case "Pengeluaran":
                        previewReport("pengeluaran.jasper", parameter, LP);
                        break;
                    case "Rekapitulasi Harian":
                        previewReport("reportharian.jasper", parameter, LP);
                        break;
                    case "Rekapitulasi Mingguan":
                        previewReport("reportmingguan.jasper", parameter, LP);
                        break;
                    case "Rekapitulasi Bulanan":
                        previewReport("reportbulanan.jasper", parameter, LP);
                        break;
                    default:
                        JOptionPane.showMessageDialog(null, "Anda belum memilih laporan!");
                        break;
                }
            } else {
            }
        }
    }//GEN-LAST:event_btCetakReportActionPerformed

