     private void login() {
        String username = tusername.getText();
        String password = tpassword.getText();
        int u_id = 0;
        String u_nama = null, u_alamat = null, u_telepon = null, u_email = null, u_posisi = null;
        
        try {
            String sql = "SELECT * FROM admin WHERE username = '" + username + "' AND password = '" + password + "'";
            Statement st = conn.createStatement();
            ResultSet rsLogin = st.executeQuery(sql);

            while (rsLogin.next()) {
                u_id = rsLogin.getInt("user_id");
                u_nama = rsLogin.getString("nama");
                u_alamat = rsLogin.getString("alamat");
                u_telepon = rsLogin.getString("notelp");
                u_email = rsLogin.getString("email");
                u_posisi = rsLogin.getString("posisi");
            }
            rsLogin.last(); //mengecek jumlah baris pada hasil query
            if (rsLogin.getRow()==1){
                UserSession.setU_id(u_id);
                UserSession.setU_nama(u_nama);
                UserSession.setU_alamat(u_alamat);
                UserSession.setU_telepon(u_telepon);
                UserSession.setU_email(u_email);
                UserSession.setU_posisi(u_posisi);
                UserSession.setU_username(username);
                UserSession.setU_password(password);
                                
                new Utama().setVisible(true);
                this.dispose();
            } else {
                JOptionPane.showMessageDialog(null, "Maaf, Username / Password salah!");
                tusername.setText("");
                tpassword.setText("");
                tpassword.requestFocus();
            }
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Terjadi Error", "Peringatan", JOptionPane.WARNING_MESSAGE);
        }
    }

